import React, { Fragment, useEffect, useState } from "react";
import { useAlert } from "react-alert";
import { useDispatch, useSelector } from "react-redux";
import { getOrderDetails } from "../../actions/orderActions";
import { Link } from 'react-router-dom'
import { MDBDataTableV5 } from 'mdbreact'
import Loader from "../layout/Loader";
export default function CheckProduct({match}) {
  const alert = useAlert();
  const dispatch = useDispatch();

  const [email , setEmail] = useState("")
  const  [number,setNumber ]= useState('')

  const handleNumber=(e)=>{
    setNumber(e.target.value)
  }
  const handleEmail=(e)=>{
    setEmail(e.target.value)
  }

  const { loading, order = {} } = useSelector(state => state.orderDetails)
  const {  shippingInfo, orderItems, paymentInfo, user, totalPrice, orderStatu } = order

  // const { error, orders } = useSelector(state => state.allOrders)

  const orderId = match.params.id;
  
  // const CheckToProduct = () => {
  //   dispatch(getOrderDetails(orderId));
  //   // alert.success("Đã thêm vào giỏ hàng");
  // };

  const CheckToProduct = () => {
    if (number && email) {
      dispatch(getOrderDetails(number, email));
    } else {
      alert.error("Please enter order number and email");
    }
  };

  useEffect(() => {
    if (orderId) {
      dispatch(getOrderDetails(orderId));
    }
  }, [dispatch, orderId]);



  const setOrders = () => {
    const data = {
        columns: [
            {
                label: 'ID',
                field: 'id',
                sort: 'asc'
            },
            {
                label: 'Số lượng',
                field: 'numOfItems',
                sort: 'asc'
            },
            {
                label: 'Tổng tiền',
                field: 'amount',
                sort: 'asc'
            },
            {
                label: 'Trạng thái',
                field: 'status',
                sort: 'asc'
            },
            {
                label: 'Hành động',
                field: 'actions',
                sort: 'asc'
            },
        ],
        rows: []
    }

    orders.forEach(orders => {
        data.rows.push({
            id: orders._id,
            numOfItems: orders.orderItems.length,
            amount: `${(orders.totalPrice).toLocaleString()}đ`,
            status: order.orderStatus && String(order.orderStatus).includes('Đã giao hàng')
                ? <p style={{ color: 'green' }}>{order.orderStatus}</p>
                : <p style={{ color: 'red' }}>{order.orderStatus}</p>,
            actions:
                <Link to={`/order/${order._id}`} className="btn btn-primary">
                    <i className="fa fa-eye"></i>
                </Link>
        })
    })

    return data;
}
const isPaid = paymentInfo && paymentInfo.status === 'succeeded' ? true : false
  return (
    <Fragment>
    <form className="form-input">
      <h1>Kiểm tra đơn hàng</h1>
      <span>
        Nhập mã đơn hàng bạn phía dưới đây để kiểm tra trạng thái đơn hàng hiện
        tại.{" "}
      </span>
      <input type="text" name="id" value={number} placeholder="Mã đơn hàng(SB-0)" onChange={handleNumber}></input>

      <input type="email" name="eMAIL" value={email} placeholder="Email" onChange={handleEmail}></input>

      <button type="button" onClick={CheckToProduct} className="w-100">
        Kiểm Tra Ngay
      </button>
    </form>
         <div>
      {loading ? <Loader></Loader> :(
        <div>
            <h2 className="my-5" >Đơn hàng # : {order._id}</h2>
            <h4 className="my-4">Tình trạng thanh toán</h4>
                                    <p className={isPaid ? "greenColor" : "redColor"}><b>{isPaid ? "Đã thanh toán" : "Chưa thanh toán"}</b></p>
            <h2 className="my-5" >Tên khách hàng {user && user.name}</h2>
            <h2 className="my-5" >Email Khach Hang : {user && user.email}</h2>
            <p><b>Tổng tiền:</b> {totalPrice} VNĐ</p>
        </div>
      )}
    <MDBDataTableV5
                data={setOrders()}
                className="px-3"
                bordered
                striped
                hover
              />
      
         </div>
    </Fragment>
  )
}
