import React, { useCallback, useEffect } from "react";
import "./Contact.css";
import { useState } from "react";

import { newMessage } from "../../actions/messageActions";
import { useAlert } from "react-alert";
import { useDispatch, useSelector } from "react-redux";
export default function Contact() {
  const { message, setMessage } = useState({
    name: "",
    email: "",
    phone: "",
    mess: "",
  });

  const { name , email , phone , mess} = message

    const alert = useAlert()
    const dispatch= useDispatch()

  const { error , success} = useSelector((state) => state.message)

  useEffect(()=>{

  })

  const HandleSubmit = (e) => {

    e.preventDefault();

    const formData = new FormData();
    formData.set("name", name);
    formData.set("email", email);
    formData.set("phone", phone);
    formData.set("mess", mess);

    dispatch(newMessage(formData));
  };



  return (
    <form className="form-input" >
      <h1>
        Contact <span>Here</span>
      </h1>
      <input type="text" name="name" placeholder="Enter name" value={name}></input>
      <input type="email" name="email" placeholder="example@gmail.com" value={email}></input>
      <input type="phone" name="phone" placeholder="+81" value={phone}></input>
      <textarea
        type="Message"
        value={mess}
        name=""
        cols="30"
        rows="10"
        placeholder="Type here...."
      ></textarea>
      <button type="submit" onClick={HandleSubmit}>Send</button>
    </form>
  );
}
