import React, { Fragment, useEffect } from "react";
import { Link } from "react-router-dom";
import { MDBDataTable } from "mdbreact";

import MetaData from "../layout/MetaData";
import Loader from "../layout/Loader";
import Sidebar from "./Sidebar";

import { useAlert } from "react-alert";
import { useDispatch, useSelector } from "react-redux";
import { allUsers, clearErrors } from "../../actions/userActions";
import { DELETE_USER_RESET } from "../../constants/userConstants";

const UsersList = ({ history }) => {
  const alert = useAlert();
  const dispatch = useDispatch();

  const { loading, error, users } = useSelector((state) => state.allUsers);
  const { isDeleted } = useSelector((state) => state.user);

  useEffect(() => {
    dispatch(allUsers());

    if (error) {
      alert.error(error);
      dispatch(clearErrors());
    }

    if (isDeleted) {
      alert.success("Xóa  tài khoản thành công");
      history.push("/admin/users");
      dispatch({ type: DELETE_USER_RESET });
    }
  }, [dispatch, alert, error, isDeleted, history]);

  const setUsers = () => {
    const data = {
      columns: [
        {
          label: "ID",
          field: "id",
          sort: "asc",
        },
        {
          label: "Tên người dùng",
          field: "name",
          sort: "asc",
        },
        {
          label: "Email",
          field: "email",
          sort: "asc",
        },
        {
          label: "Quyền",
          field: "role",
          sort: "asc",
        },
        {
          label: "Trạng thái tài khoản    ",
          field: "status",
          sort: "asc",
        },
        {
          label: "Hành động",
          field: "actions",
        },
      ],
      rows: [],
    };

    users.forEach((userr) => {
      let hoat_dong = "";
      if (userr.role === "look") {
        userr.role = "Đã bị khóa";
      }
      if (userr.role === "user") {
        userr.role = "Người dùng";
      }
      if (userr.role === "admin") {
        userr.role = "Quản trị";
      }
      if (userr.role === "Người dùng" || "Quản trị") {
        hoat_dong = "Hoạt động";
      }

      data.rows.push({
        id: userr._id,
        name: userr.name,
        email: userr.email,
        role: userr.role,
        status:
          userr.role && String(userr.role).includes("Đã bị khóa") ? (
            <p style={{ color: "red" }}>
              <i class="bi bi-key-fill"></i> {userr.role}
            </p>
          ) : (
            <p style={{ color: "green" }}>{hoat_dong}</p>
          ),
        actions: (
          <Fragment>
            &emsp;
            <Link
              to={`/admin/user/${userr._id}`}
              className="btn btn-primary py-1 px-2"
            >
              <i className="fa fa-pencil"></i>
            </Link>
            &emsp;
            <Link
              to={`/admin/look_user/${userr._id}`}
              className="btn btn-danger py-1 px-2"
            >
              <i class="bi bi-key-fill"></i>
            </Link>
          </Fragment>
        ),
      });
    });

    return data;
  };

  return (
    <Fragment>
      <MetaData title={"All Users"} />
      <div className="row">
        <div className="col-12 col-md-2">
          <Sidebar />
        </div>

        <div className="col-12 col-md-10">
          <Fragment>
            <h1 className="my-5">Tất cả người dùng</h1>

            {loading ? (
              <Loader />
            ) : (
              <MDBDataTable
                data={setUsers()}
                className="px-3"
                bordered
                striped
                hover
              />
            )}
          </Fragment>
        </div>
      </div>
    </Fragment>
  );
};

export default UsersList;
