import React, { Fragment, useEffect, useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { useAlert } from "react-alert";
import { getProducts , ascendingProduct } from "../../actions/productActions";
import Product from "./Product";

export default function ProductList({ match }) {
  const dispatch = useDispatch();
  const alert = useAlert();
  const { products, error } = useSelector((state) => state.products);
  const keywords = match.params.keyword;

  const [category, setCategory] = useState("");

  const categories = [
    "Hoa Bó",
    "Hoa Khai Trương",
    "Hoa Vip",
    "Hoa Giỏ",
    "Hoa Sinh Nhật",
    "Hoa Sự Kiện",
    "Hoa Chia Buồn",
  ];

  const [selectedOption, setSelectedOption] = useState('Sắp xếp theo');
 
  const handleSort = () =>{
    if(products){
      dispatch(ascendingProduct());
      products.sort((a,b)=>a.price - b.price)
    
    }
    else{
      console.log('1231231231')
    }
  }


