  // }, [dispatch, alert, error, keywords,selectedOption]);


  return (
    <Fragment>
 


          {/* <div>
          <h1 className="text-center">Danh muc san pham</h1>
         <ul id="ul_top_hypers">
          {categories.map((category) => (
            <li
              style={{
                cursor: "pointer",
                listStyleType: "none",
              }}
              key={category}
              onClick={() => setCategory(category)}
            >
              <div className="button">
                <p className="btnText">{category}</p>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <br></br> */}


      <section id="products" className="container mt-5" >
  

  <select className="p-2 rounded" >
       <option value="">Sắp xếp theo</option>
       <option value="option1">Gia từ cao đến thấp</option>
       <option value="Giá từ thấp đến cao" onClick={handleSort}>Giá từ thấp đến cao</option>
       <option value="option3">Option 3</option>
     </select>

        {
          <div className="row">
            {keywords ? (
              <Fragment>
                <div className="col-6 col-md-9">
                  <div className="row">
                    {products?.map((product) => (
                      <Product key={product._id} product={product} col={4} />
                    ))}
                  </div>
                </div>
              </Fragment>
            ) : (
              products?.map((product) => (
                <Product key={product._id} product={product} col={3}  />
              ))
            )}
          </div>
        }
      </section>
    </Fragment>
  );
}
