import React, { Fragment } from "react";
import { useAlert } from "react-alert";
import { useDispatch, useSelector } from "react-redux";
import { getOrderDetails } from "../../actions/orderActions";
import { Link } from 'react-router-dom'

import { MDBDataTableV5 } from 'mdbreact'
export default function CheckProduct({match}) {
  const alert = useAlert();
  const dispatch = useDispatch();

  const { loading, error, order } = useSelector(state => state.orderDetails)
  // const {  user } = order
  const CheckToProduct = () => {
    dispatch(getOrderDetails(match.params.id));
    // alert.success("Đã thêm vào giỏ hàng");
  };
  const setOrders = () => {
    const data = {
        columns: [
            {
                label: 'ID',
                field: 'id',
                sort: 'asc'
            },
            {
                label: 'Số lượng',
                field: 'numOfItems',
                sort: 'asc'
            },
            {
                label: 'Tổng tiền',
                field: 'amount',
                sort: 'asc'
            },
            {
                label: 'Trạng thái',
                field: 'status',
                sort: 'asc'
            },
            {
                label: 'Hành động',
                field: 'actions',
                sort: 'asc'
            },
        ],
        rows: []
    }

    order.forEach(order => {
        data.rows.push({
            id: order._id,
            numOfItems: order.orderItems.length,
            amount: `${(order.totalPrice).toLocaleString()}đ`,
            status: order.orderStatus && String(order.orderStatus).includes('Đã giao hàng')
                ? <p style={{ color: 'green' }}>{order.orderStatus}</p>
                : <p style={{ color: 'red' }}>{order.orderStatus}</p>,
            actions:
                <Link to={`/order/${order._id}`} className="btn btn-primary">
                    <i className="fa fa-eye"></i>
                </Link>
        })
    })

    return data;
}


  
  return (
    <Fragment>
    <form className="form-input">
      <h1>Kiểm tra đơn hàng</h1>
      <span>
        Nhập mã đơn hàng bạn phía dưới đây để kiểm tra trạng thái đơn hàng hiện
        tại.{" "}
      </span>
      <input type="text" name="name" placeholder="Mã đơn hàng(SB-0)"></input>

      <input type="email" name="eMAIL" placeholder="Email"></input>

      <button type="button" onClick={CheckToProduct} className="w-100">
        Kiểm Tra Ngay
      </button>
    </form>
    <div>
    <MDBDataTableV5
                    data={setOrders()}
                    className="px-3"
                    bordered
                    striped
                    hover
                />

    </div>
    </Fragment>
  )
}
