import React, { Fragment, useEffect, useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { useAlert } from "react-alert";
import { getProducts , ascendingProduct } from "../../actions/productActions";
import Product from "./Product";
import Partner from "../layout/Partner";
export default function ProductList({ match }) {
  const dispatch = useDispatch();
  const alert = useAlert();
  const { products, error } = useSelector((state) => state.products);
  const keywords = match.params.keyword;

  const [category, setCategory] = useState("");

  const categories = [
    "Hoa Bó",
    "Hoa Khai Trương",
    "Hoa Vip",
    "Hoa Giỏ",
    "Hoa Sinh Nhật",
    "Hoa Sự Kiện",
    "Hoa Chia Buồn",
  ];

  const [selectedOption, setSelectedOption] = useState('Sắp xếp theo');
  const [optionData, setOptionData] = useState(null);
  const handleOptionChange = async(event) => {
    const selectedValue= event.target.value
    setSelectedOption(event.target.value);
    try {
      const response = dispatch(ascendingProduct());
      setOptionData(response);
    } catch (error) {
      console.error('Error fetching option data:', error);
    }
  };

  
  // const options = [
  //   'Sắp xếp theo',
  //   'Giá từ thấp đến cao',
  //   'Giá từ cao đến thấp',
  // ];

  // useEffect(() => {
  //   if (error) {
  //     return alert.error(error);
  //   }

  //   // dispatch(getProducts(keywords, currentPage));
  //   dispatch(getProducts(keywords));
  // }, [dispatch, alert, error, keywords]);


  // const keywords = match.params.keyword;
  return (
    <Fragment>
 <div>

 <select value={selectedOption} onChange={handleOptionChange}>
        {/* <option value="">Select an option</option> */}
        <option value="option1">Option 1</option>
        <option value="Giá từ thấp đến cao">Giá từ thấp đến cao</option>
        <option value="option3">Option 3</option>
      </select>
    </div>


          <div>
          <h1 className="text-center">Danh muc san pham</h1>
         <ul id="ul_top_hypers">
          {categories.map((category) => (
            <li
              style={{
                cursor: "pointer",
                listStyleType: "none",
              }}
              key={category}
              onClick={() => setCategory(category)}
            >
              <div className="button">
                <p className="btnText">{category}</p>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <br></br>
      


      <section id="products" className="container mt-5">
        {
          <div className="row">
            {keywords ? (
              <Fragment>
                <div className="col-6 col-md-9">
                  <div className="row">
                    {products?.map((product) => (
                      <Product key={product._id} product={product} col={4} />
                    ))}
                  </div>
                </div>
              </Fragment>
            ) : (
              products?.map((product) => (
                <Product key={product._id} product={product} col={3} />
              ))
            )}
          </div>
        }
      </section>
    </Fragment>
  );
}
