        visible={isShowContact}
        render={(attrs) => (
          <S.WrapperMenu>
            <S.ItemMenu href="tel:0379967723" title="Gọi trực tiếp">
            <img src={IconPhone} className="my-2"></img>
                <p>0379967723</p>
            </S.ItemMenu>
            <S.ItemMenu href="tel:0379967723" title="Gọi trực tiếp">   
              <img src={IconPhone1} className="my-2"></img>
                <p>0382510848</p>
            </S.ItemMenu>
            <S.ItemMenu
              href="https://zalo.me/0901958595"
              target="_blank"
              title="Chat qua Zalo"
            >
              <img src={IconZalo} alt="iconzalo"></img>
              <span>Nhắn tin Zalo</span>
