import React, { Fragment, useEffect, useState } from "react";
import Pagination from "react-js-pagination";

import MetaData from "./layout/MetaData";
import Product from "./product/Product";
import Loader from "./layout/Loader";
import "./layout/Header.css";
import { useDispatch, useSelector } from "react-redux";
import { useAlert } from "react-alert";
import { getProducts } from "../actions/productActions";
import Contact from "./Contact/Contact";
import Page from "../components/layout/Page";
import About from "./layout/About";
import Partner from "./layout/Partner";

// const { createSliderWithTooltip } = Slider;
// const Range = createSliderWithTooltip(Slider.Range);

const Home = ({ match }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [price, setPrice] = useState([1, 1000000]);
  const [rating, setRating] = useState(0);
  const [category, setCategory] = useState("");

  const categories = [
    "Hoa Bó",
    "Hoa Khai Trương",
    "Hoa Vip",
    "Hoa Giỏ",
    "Hoa Sinh Nhật",
    "Hoa Sự Kiện",
    "Hoa Chia Buồn",
  ];

  const alert = useAlert();
  const dispatch = useDispatch();

  const {
    loading,
    products,
    error,
    productsCount,
    resPerPage,
    filteredProductsCount,
  } = useSelector((state) => state.products);

  const keywords = match.params.keyword;

  useEffect(() => {
    if (error) {
      return alert.error(error);
    }

    dispatch(getProducts(keywords, currentPage, price, category, rating));
    //   dispatch(getProducts(keywords));
    // }, [dispatch, alert, error, keywords]);
  }, [dispatch, alert, error, keywords, currentPage, price, category, rating]);

  function setCurrentPageNo(pageNumber) {
    setCurrentPage(pageNumber);
  }

  let count = productsCount;
  if (keywords) {
    count = filteredProductsCount;
  }
  return (
    <Fragment>
      <br />
      <h3 className="text-center">Danh mục sản phẩm</h3>
      <div id="products_heading" className="container container-fluid">
        <ul id="ul_top_hypers">
          {categories.map((category) => (
            <li
              style={{
                cursor: "pointer",
                listStyleType: "none",
              }}
              key={category}
              onClick={() => setCategory(category)}
            >
              <div className="button">
                <p className="btnText">{category}</p>
                <div className="btnTwo">
                  <p className="btnText2">Chọn!</p>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <br />
      {/* /<slideCategoryy /> */}
      <Page></Page>
      <Partner></Partner>
      <About></About>

      <br />
      {loading ? (
        <Loader />
      ) : (
        <Fragment>
          <MetaData title={"Website bán hàng trực tuyến"} />

          <h1 id="products_heading" className="container container-fluid">
            &emsp;&emsp;&emsp;
          </h1>
          <h3 className="heading">Sản phẩm</h3>

          <section id="products" className="container mt-5">
            {
              <div className="row">
                {keywords ? (
                  <Fragment>
                    <div className="col-6 col-md-3 mt-5 mb-5">
                      <div className="px-5">
                        <h4 className="mb-5">Khoảng giá</h4>
                        {/* <Range
                          marks={{
                            10000: `10.000`,
                            1000000: `1.000.000`,
                          }}
                          min={10}
                          max={1000000}
                          defaultValue={[1, 1000000]}
                          tipFormatter={(value) => `${value}`}
                          tipProps={{
                            placement: "top",
                            visible: true,
                          }}
                          value={price}
                          onChange={(price) => setPrice(price)}
                        /> */}
                        <br />
                        <hr className="my-3" />

                        <div className="mt-5">
                          <h4 className="mb-3">Đánh giá</h4>

                          <ul className="pl-0">
                            {[5, 4, 3, 2, 1].map((star) => (
                              <li
                                style={{
                                  cursor: "pointer",
                                  listStyleType: "none",
                                }}
                                key={star}
                                onClick={() => setRating(star)}
                              >
                                <div className="rating-outer">
                                  <div
                                    className="rating-inner"
                                    style={{
                                      width: `${star * 20}%`,
                                    }}
                                  ></div>
                                </div>
                              </li>
                            ))}
                          </ul>
                        </div>
                      </div>
                    </div>

                    <div className="col-6 col-md-9">
                      <div className="row">
                        {products?.map((product) => (
                          <Product
                            key={product._id}
                            product={product}
                            col={4}
                          />
                        ))}
                      </div>
                    </div>
                  </Fragment>
                ) : (
                  products?.map((product) => (
                    <Product key={product._id} product={product} col={3} />
                  ))
                )}
              </div>
            }
          </section>
          {resPerPage <= count && (
            <div className="d-flex justify-content-center mt-5">
              <Pagination
                activePage={currentPage}
                itemsCountPerPage={resPerPage}
                totalItemsCount={productsCount}
                onChange={setCurrentPageNo}
                nextPageText={"Tiếp"}
                prevPageText={"Trở về"}
                firstPageText={"Đầu tiên"}
                lastPageText={"Cuối cùng"}
                itemClass="page-item"
                linkClass="page-link"
              />
            </div>
          )}
        </Fragment>
      )}
      <Contact></Contact>
      <section>
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <div id="map">
                <iframe
                  src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3918.4195585228513!2d106.782527774909!3d10.855659084037605!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3175276e7ea103df%3A0xb6cf10bb7d719327!2sThu%20Duc%20Campus%20Hutech%20khu%20E!5e0!3m2!1svi!2s!4v1686568260119!5m2!1svi!2s"
                  width="100%"
                  height="500"
                  loading="lazy"
                  referrerpolicy="no-referrer-when-downgrade"
                ></iframe>
              </div>
            </div>
          </div>
        </div>
      </section>
    </Fragment>
  );
};

export default Home;
